import mongoose from 'mongoose';

const fields = {
  user: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'User',
    required: true,
  },
  hash: {
    type: String,
    required: true,
  },
  lastUsed: {
    type: Date,
    default: () => new Date(),
    expires: '10s',
  },
};

const config = {
  collection: 'tokens',
};

const schema = new mongoose.Schema(fields, config);

schema.pre('save', function(next) {
  this.lastUsed = new Date();
  return next();
});

const model = mongoose.model('Token', schema);

export default model;
